function handleSSEEvent(data) {
  switch(data.type) {
    case 'connection_established':
      console.log('‚úÖ Conectado ao SSE');
      showNotification('Conectado!', 'success');
      break;

    case 'webhook_event':
      handleWebhookEvent(data.event, data.data);
      break;

    case 'notification':
      showNotification(data.message, data.level);
      break;

    case 'heartbeat':
      // Opcional: mostrar indicador de conex√£o ativa
      updateConnectionStatus('connected');
      break;

    case 'test':
      console.log('üß™ Teste SSE:', data.message);
      break;

    case 'custom_event':
      handleCustomEvent(data.event, data.data);
      break;
  }
}

function handleWebhookEvent(eventType, eventData) {
  switch(eventType) {
    case 'order.created':
      showNewOrderNotification(eventData.order);
      updateOrdersList();
      playNotificationSound();
      break;

    case 'order.status_changed':
      updateOrderStatus(eventData.order.id, eventData.new_status);
      break;

    case 'order.cancelled':
      showOrderCancelledNotification(eventData.order);
      break;

    case 'store.viewed':
      incrementVisitorCounter();
      break;

    case 'store.product_viewed':
      updateProductViews(eventData.product.id);
      break;
  }
}

