// Hook personalizado para SSE
import { useEffect, useRef, useState } from 'react';

export function useSSE(storeId, token) {
  const [isConnected, setIsConnected] = useState(false);
  const [events, setEvents] = useState([]);
  const eventSourceRef = useRef(null);

  useEffect(() => {
    if (!storeId || !token) return;

    const url = `http://localhost:3000/api/v1/sse/connect/${storeId}?token=${token}`;
    eventSourceRef.current = new EventSource(url);

    eventSourceRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'connection_established') {
        setIsConnected(true);
      }

      setEvents(prev => [...prev.slice(-49), data]); // Manter √∫ltimos 50 eventos
    };

    eventSourceRef.current.onerror = () => {
      setIsConnected(false);
    };

    return () => {
      eventSourceRef.current?.close();
    };
  }, [storeId, token]);

  return { isConnected, events };
}

