// Componente para exibir notifica√ß√µes
import { useSSE } from './useSSE';

export function SSENotifications({ storeId }) {
  const token = localStorage.getItem('auth_token');
  const { isConnected, events } = useSSE(storeId, token);

  const newOrders = events.filter(e => 
    e.type === 'webhook_event' && e.event === 'order.created'
  );

  return (
    <div className="sse-notifications">
      <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
        {isConnected ? 'üü¢ Conectado' : 'üî¥ Desconectado'}
      </div>

      {newOrders.map((orderEvent, index) => (
        <div key={index} className="new-order-notification">
          üîî Novo pedido: #{orderEvent.data.order.numero_pedido}
          <br />
          Cliente: {orderEvent.data.order.cliente_nome}
          <br />
          Total: R$ {orderEvent.data.order.total.toFixed(2)}
        </div>
      ))}
    </div>
  );
}

composable.code:
// Composable Vue 3
import { ref, onMounted, onUnmounted } from 'vue';

export function useSSE(storeId, token) {
  const isConnected = ref(false);
  const events = ref([]);
  let eventSource = null;

  onMounted(() => {
    if (!storeId.value || !token.value) return;

    const url = `http://localhost:3000/api/v1/sse/connect/${storeId.value}?token=${token.value}`;
    eventSource = new EventSource(url);

    eventSource.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'connection_established') {
        isConnected.value = true;
      }

      events.value.push(data);

      // Manter apenas √∫ltimos 50 eventos
      if (events.value.length > 50) {
        events.value = events.value.slice(-50);
      }
    };

    eventSource.onerror = () => {
      isConnected.value = false;
    };
  });

  onUnmounted(() => {
    eventSource?.close();
  });

  return { isConnected, events };
}

